//react imports
import React from 'react'
import ReviewRatingStars from './ReviewRatingStars'

//bootstrap imports
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

const ReviewCards = ({reviews}) => {
  return (
    <section className="w-full rounded grid grid-cols-3">
      {reviews && reviews.map(review => {
        return (
          <article className="font-bold text-xl m-2 border bg-blue-200 rounded" key={review.id}>
            <h3 class="font-bold text-xl mb-2" >{review.name}</h3>
            <p class="text-gray-700 text-base">
              {review.review}
            </p>
          </article>
        )
      })}        
    </section>


    /* <section className='flex items-center justify-center gap-1 flex-wrap m-1'>
      {reviews && 
        reviews.map(review => {
          return (
            <Card key={review.id} style={{ width: '12rem', height: '8rem', backgroundColor:'rgb(78, 195, 234)' }}>
                <Card.Body>
                  <ReviewRatingStars rating={review.rating} />
                  <Card.Title>{review.name}</Card.Title>                  
                  <Card.Text>{review.review}</Card.Text>
                </Card.Body>
              </Card>
          )          
        })
      }
    </section> */
  )
}

export default ReviewCards
