import React from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import ReviewRatingStars from './ReviewRatingStars'
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa'
import { useGlobalContext } from '../globalContext'

const ReviewCards = ({reviews}) => {
  const {id} = useParams()
  const { reviewIndex, setReviewIndex } = useGlobalContext()

  const handleDeleteReview = async (id, reviewId) => {
    try {
      await axios.delete(`/${id}/reviews/${reviewId}`)
      window.location.reload(true);
    } catch (error) {
      console.log(error)
    }    
  }
  const prevReview = () => {
    setReviewIndex(index => {
      if (index === 0) {
        return reviews.length - 1
      } else {
        return index - 1
      }
    })
  }
  const nextReview = () => {
    setReviewIndex(index => {
      if (index === reviews.length - 1) {
        return 0
      } else {
        return index + 1
      }
    })
  }

  return (
      <article className="w-full flex items-center justify-center h-48 py-2 m-2 border shadow bg-blue-200 rounded" key={reviews[reviewIndex].id}>
            <FaChevronLeft
              className=''
              onClick={() => prevReview()}
            />
            <div className=''>
            <ReviewRatingStars rating={reviews[reviewIndex].rating} />
            <h3 className=''>{reviews[reviewIndex].name}</h3>
            <p>{reviews[reviewIndex].rating.review}</p>
            </div>
            <FaChevronRight
              className=''
              onClick={() => nextReview()}
            />
      </article>

      /* {reviews && reviews.map(review => {
        return (
          <article className="flex flex-col items-center justify-center h-28 py-2 m-2 border shadow bg-blue-200 rounded" key={review.id}>
            <FaTimes
              className='text-red-600 self-end mr-2 cursor-pointer'
              onClick={() => handleDeleteReview(id, review.id)}
            />
            <ReviewRatingStars rating={review.rating} />
            <h3 className="font-bold text-xl mb-2 break-all" >{review.name}</h3>
            <p className="w-full h-24 px-2 text-gray-700 text-base break-all">
              {review.review}
            </p>
          </article>
        )
      })} */
  )
}

export default ReviewCards
