import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'

const ReviewRestaurantForm = () => {
    const {id} = useParams()
    const {
        reviewerName, setReviewerName,
        reviewerRating, setReviewerRating,
        reviewerText, setReviewerText
    } = useGlobalContext()
    
    // Need to fetch the data inside this component.
    // Otherwise, if the user copy and paste the URL
    // in other tab, the browser won't be able to find the data.
    useEffect(() => {
        const fetchData = async () => {
            try {
                await axios.get(`/${id}`)
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()
    }, [])

    const handleReview = event => {
        event.preventDefault()
    }   

    return (
        <section className='review-form'>
            <form>
                <div className='row'>
                    <label htmlFor="reviewerName">Name:</label>
                    <input
                        id='reviewerName'
                        type="text"
                        value={reviewerName}
                        onChange={event => {event.target.value}}
                    />

                    <label htmlFor="rating">Rating:</label>
                    <select
                        id='rating'
                        type="text"
                        value={reviewerRating}
                        onChange={event => {event.target.value}}
                    >
                        <option disabled>Rating</option>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>
                </div>

                <label htmlFor="review">Review:</label>
                <textarea
                    id="review"
                    cols="78"
                    rows="10"
                    value={reviewerText}
                    onChange={event => {event.target.value}}
                />

                <button
                    type='submit'
                    
                >Submit</button>
            </form>
        </section>
    )
}

export default ReviewRestaurantForm
