//react imports
import React, { useEffect, useCallback } from 'react'
import { Link } from 'react-router-dom'
import axios from '../apis/axios' //from axios to fetch the data
import { useGlobalContext } from '../globalContext'
import ReviewRatingStars from './ReviewRatingStars'

//bootstrap imports
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'

const RestaurantList = () => {
    const { allRestaurants, setAllRestaurants } = useGlobalContext()

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('/')
                setAllRestaurants(response.data.restaurants)
            } catch (error) {
                console.log(error)
            }
        }
        fetchData()
    }, [allRestaurants])

    // The data fetching above has an infinite loop.
    // Tried to fix that with useCallback below, but did not work.
    // const fetchData = useCallback(async () => {
    //     console.log(allRestaurants)
    //         try {
    //             const response = await axios.get('/')
    //             setAllRestaurants(response.data.restaurants)
    //         } catch (error) {
    //             console.log(error)
    //         }
    //     }, [allRestaurants])
    //     useEffect(() => {
    //         fetchData()
    //     }, [allRestaurants, fetchData])        

    const handleDelete = async id => {
        try {
            await axios.delete(`/${id}`)
        } catch (error) {
            console.log(error)
        }
    }

    const renderRating = restaurant => {
        if (!restaurant.total_ratings) {
            return <span>None</span>
        } else {
            return (
                <>
                    <ReviewRatingStars rating={restaurant.average_rating} />
                    <span>({restaurant.total_ratings})</span>
                </>
            )
        }
    }

    //rendering component
    return (
        <table class="table-auto">
            <thead>
                <tr>
                    <th className='align-middle'>Restaurant</th>
        //             <th>Location</th>
        //             <th>Price Range</th>
        //             <th>Ratings</th>
        //             <th>Review</th>
        //             <th>Edit</th>
        //             <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td>The Sliding Mr. Bones (Next Stop, Pottersville)</td>
                <td>Malcolm Lockyer</td>
                <td>1961</td>
                </tr>
                <tr>
                <td>Witchy Woman</td>
                <td>The Eagles</td>
                <td>1972</td>
                </tr>
                <tr>
                <td>Shining Star</td>
                <td>Earth, Wind, and Fire</td>
                <td>1975</td>
                </tr>
            </tbody>
        </table>


        // <Table bordered hover>
        //     <thead className='text-white text-center align-middle' style={{ backgroundColor: 'skyblue' }}>
        //         <tr>
        //             <th className='align-middle'>Restaurant</th>
        //             <th>Location</th>
        //             <th>Price Range</th>
        //             <th>Ratings</th>
        //             <th>Review</th>
        //             <th>Edit</th>
        //             <th>Delete</th>
        //         </tr>
        //     </thead>
        //     <tbody className='text-center align-middle'>
        //         {allRestaurants && allRestaurants.map(restaurant => {
        //             return (
        //                 <tr key={restaurant.id}>
        //                     <td>{restaurant.name}</td>
        //                     <td>{restaurant.location}</td>
        //                     <td>{'$'.repeat(restaurant.price_range)}</td>
        //                     <td>{renderRating(restaurant)}</td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}`}>
        //                             <Button className='btn-sm' variant='primary'>
        //                                 Review
        //                             </Button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}/update`}>
        //                             <Button className='btn-sm' variant='warning'>
        //                                 Update
        //                             </Button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <Button className='btn-sm' variant='danger' onClick={() => handleDelete(restaurant.id)}>
        //                             Delete
        //                         </Button>
        //                     </td>
        //                 </tr>
        //             )
        //         })}
        //     </tbody>
        // </Table>
    )
}

export default RestaurantList
