//react imports
import React, { useEffect, useCallback } from 'react'
import { Link } from 'react-router-dom'
import axios from '../apis/axios' //from axios to fetch the data
import { useGlobalContext } from '../globalContext'
import ReviewRatingStars from './ReviewRatingStars'

//bootstrap imports
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'

const RestaurantList = () => {
    const { allRestaurants, setAllRestaurants } = useGlobalContext()

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('/')
                setAllRestaurants(response.data.restaurants)
            } catch (error) {
                console.log(error)
            }
        }
        fetchData()
    }, [allRestaurants])

    // The data fetching above has an infinite loop.
    // Tried to fix that with useCallback below, but did not work.
    // const fetchData = useCallback(async () => {
    //     console.log(allRestaurants)
    //         try {
    //             const response = await axios.get('/')
    //             setAllRestaurants(response.data.restaurants)
    //         } catch (error) {
    //             console.log(error)
    //         }
    //     }, [allRestaurants])
    //     useEffect(() => {
    //         fetchData()
    //     }, [allRestaurants, fetchData])        

    const handleDelete = async id => {
        try {
            await axios.delete(`/${id}`)
        } catch (error) {
            console.log(error)
        }
    }

    const renderRating = restaurant => {
        if (!restaurant.total_ratings) {
            return <span>None</span>
        } else {
            return (
                <>
                    <ReviewRatingStars rating={restaurant.average_rating} />
                    <span className='text-yellow-500'>({restaurant.total_ratings})</span>
                </>
            )
        }
    }

    //rendering component
    return (
        <table class="w-full border-collapse border border-slate-400 text-sm text-center">
            <thead className='bg-blue-500 text-white'>
                <tr>
                    <th className="border border-slate-300">Restaurant</th>
                    <th className="border border-slate-300">Location</th>
                    <th className="border border-slate-300">Price Range</th>
                    <th className="border border-slate-300">Ratings</th>
                    <th className="border border-slate-300">Review</th>
                    <th className="border border-slate-300">Edit</th>
                    <th className="border border-slate-300">Delete</th>
                </tr>
            </thead>
            <tbody>
                {allRestaurants && allRestaurants.map(restaurant => {
                    return (
                        <tr key={restaurant.id} className='hover:bg-blue-100'>
                            <td className="border border-slate-300">{restaurant.name}</td>
                            <td className="border border-slate-300">{restaurant.location}</td>
                            <td className="border border-slate-300">{'$'.repeat(restaurant.price_range)}</td>
                            <td className="border border-slate-300">{renderRating(restaurant)}</td>
                            <td className="border border-slate-300">
                                <Link to={`/restaurants/${restaurant.id}`}>
                                    <button className="rounded col-span-2 
                                            bg-blue-500 hover:bg-blue-600 text-white
                                            text-sm font-bold
                                            p-1              
                                            focus:outline-none focus:shadow-outline"
                                    >
                                        Review
                                    </button>
                                </Link>
                            </td>
                            <td className="border border-slate-300 ...">
                                <Link to={`/restaurants/${restaurant.id}/update`}>
                                    <button className="rounded col-span-2 
                                            bg-yellow-500 hover:bg-yellow-600 text-white
                                            text-sm font-bold
                                            p-1            
                                            focus:outline-none focus:shadow-outline">
                                        Update
                                    </button>
                                </Link>
                            </td>
                            <td className="border border-slate-300 ...">
                                <button
                                    className="rounded col-span-2 
                                            bg-red-500 hover:bg-red-700 text-white
                                            text-sm font-bold
                                            px-1 py-2              
                                            focus:outline-none focus:shadow-outline"
                                    onClick={() => handleDelete(restaurant.id)}>
                                    Delete
                                </button>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>

        // <table className="table-auto">
        //     <thead>
        //         <tr>
        //             <th>Restaurant</th>
        //             <th>Location</th>
        //             <th>Price Range</th>
        //             <th>Ratings</th>
        //             <th>Review</th>
        //             <th>Edit</th>
        //             <th>Delete</th>
        //         </tr>
        //     </thead>
        //     <tbody>
        //         {allRestaurants && allRestaurants.map(restaurant => {
        //             return (
        //                 <tr key={restaurant.id}>
        //                     <td>{restaurant.name}</td>
        //                     <td>{restaurant.location}</td>
        //                     <td>{'$'.repeat(restaurant.price_range)}</td>
        //                     <td>{renderRating(restaurant)}</td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}`}>
        //                             <button className="rounded col-span-2 
        //                                     bg-blue-500 hover:bg-blue-600 text-white
        //                                     text-sm
        //                                     px-1 py-2              
        //                                     focus:outline-none focus:shadow-outline"
        //                             >
        //                                 Review
        //                             </button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}/update`}>
        //                             <button className="rounded col-span-2 
        //                                     bg-yellow-500 hover:bg-yellow-600 text-white
        //                                     text-sm
        //                                     px-1 py-2              
        //                                     focus:outline-none focus:shadow-outline">
        //                                 Update
        //                             </button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <button
        //                             className="rounded col-span-2 
        //                                     bg-red-500 hover:bg-red-700 text-white
        //                                     text-sm
        //                                     px-1 py-2              
        //                                     focus:outline-none focus:shadow-outline"
        //                             onClick={() => handleDelete(restaurant.id)}>
        //                             Delete
        //                         </button>
        //                     </td>
        //                 </tr>
        //             )
        //         })}
        //     </tbody>
        // </table>


        // <Table bordered hover>
        //     <thead className='text-white text-center align-middle' style={{ backgroundColor: 'skyblue' }}>
        //         <tr>
        //             <th className='align-middle'>Restaurant</th>
        //             <th>Location</th>
        //             <th>Price Range</th>
        //             <th>Ratings</th>
        //             <th>Review</th>
        //             <th>Edit</th>
        //             <th>Delete</th>
        //         </tr>
        //     </thead>
        //     <tbody className='text-center align-middle'>
        //         {allRestaurants && allRestaurants.map(restaurant => {
        //             return (
        //                 <tr key={restaurant.id}>
        //                     <td>{restaurant.name}</td>
        //                     <td>{restaurant.location}</td>
        //                     <td>{'$'.repeat(restaurant.price_range)}</td>
        //                     <td>{renderRating(restaurant)}</td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}`}>
        //                             <Button className='btn-sm' variant='primary'>
        //                                 Review
        //                             </Button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <Link to={`/restaurants/${restaurant.id}/update`}>
        //                             <Button className='btn-sm' variant='warning'>
        //                                 Update
        //                             </Button>
        //                         </Link>
        //                     </td>
        //                     <td>
        //                         <Button className='btn-sm' variant='danger' onClick={() => handleDelete(restaurant.id)}>
        //                             Delete
        //                         </Button>
        //                     </td>
        //                 </tr>
        //             )
        //         })}
        //     </tbody>
        // </Table>
    )
}

export default RestaurantList
