import React, {useEffect} from 'react'
import { useParams } from 'react-router-dom'
import { useGlobalContext } from '../context/globalContext'
import axios from '../apis/axios'

const UpdateRestaurantForm = () => {    
    const {id} = useParams()
    const {
        name, setName,
        location, setLocation,
        priceRange, setPriceRange,
    } = useGlobalContext()

    // Need to fetch the data inside this component.
    // Otherwise, if the user copy and paste the URL
    // in other tab, the browser won't be able to find the data.
    useEffect(() => {        
        const fetchData = async () => {
            const response = await axios.get(`/${id}`)
            setName(response.data.data.restaurant[0].name)
            setLocation(response.data.data.restaurant[0].location)
            setPriceRange(response.data.data.restaurant[0].price_range)
        }
        fetchData()        
    }, [])
    
    const handleUpdate = async event => {
        event.preventDefault()
        const updatedRestaurant = await axios.put(`/${id}`, {
            "name": name,
            "location": location,
            price_range: priceRange
        })
        console.log(updatedRestaurant)
    }

    console.log({
        id: id,
        name: name,
        location: location,
        price_range: priceRange
    })

    return (
        <form>
                <label htmlFor="name">Name:</label>
                <input
                    id='name'
                    type="text"
                    value={name}
                    onChange={event => setName(event.target.value)}
                />

                <label htmlFor="location">Location:</label>
                <input
                    id='location'
                    type="text"
                    value={location}
                    onChange={event => setLocation(event.target.value)}
                />

                <label htmlFor="price-range">Price Range</label>
                <select
                    id="price-range"
                    value={priceRange}
                    onChange={event => setPriceRange(event.target.value)}
                >
                    <option value="1">$</option>
                    <option value="2">$$</option>
                    <option value="3">$$$</option>
                    <option value="4">$$$$</option>
                    <option value="5">$$$$$</option>
                </select>

                <button
                    type='submit'
                    onClick={() => handleUpdate()}
                >
                    Update
                </button>
        </form>
    )
}

export default UpdateRestaurantForm
