import React, {useEffect} from 'react'
import ReviewRestaurantForm from '../components/ReviewRestaurantForm'
import ReviewRatingStars from '../components/ReviewRatingStars'
import ReviewCards from '../components/ReviewCards'
import axios from '../apis/axios'
import { useParams } from 'react-router-dom'
import { useGlobalContext } from '../globalContext'

const ReviewRestaurantPage = () => {
  const {id} = useParams()
  let response = 'test'
  const {selectedRestaurant, setSelectedRestaurant} = useGlobalContext()
  console.log('rendered component')  

  useEffect(() => {
    // console.log(selectedRestaurant)
        const fetchData = async () => {
            try {
                const response = setSelectedRestaurant(await axios.get(`/${id}`))
                console.log(response + ' inside use  Effect')
                // console.log(id)
                // console.log(selectedRestaurant)
                setSelectedRestaurant(response.data)
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()
    }, [])

    console.log(console.log(selectedRestaurant + ' outside use  Effect'))

  return (
    <>
      <h1 className='review--title'>Review Page</h1>
      <h2 className='review--subtitle'>{selectedRestaurant.restaurant.name}</h2>
      <ReviewRatingStars rating={2.5} />
      {/* <ReviewCards review={response.reviews} /> */}
      <ReviewRestaurantForm />    
    </>    
  )
}

export default ReviewRestaurantPage
