import React, {useEffect} from 'react'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const AddRestaurantForm = () => {
  const {
    restaurantName, setRestaurantName,
    restaurantLocation, setRestaurantLocation,
    restaurantPriceRange, setRestaurantPriceRange
  } = useGlobalContext()
  
  useEffect(() => {
      setRestaurantName('')
      setRestaurantLocation('')
      setRestaurantPriceRange('Price Range')
  }, [])

  const handleAdd = async event => {
    event.preventDefault()
    try {
        await axios.post('/', {
            name: restaurantName,
            location: restaurantLocation,
            price_range: restaurantPriceRange,
        })
        window.location.reload(true);
    } catch (error) {
        console.log(error)
    }
  }

  return (
      <Form className='d-flex'>
          <Form.Group className="mb-3" controlId="restaurantNameForm">
              <Form.Control
                  type="text"
                  placeholder="Restaurant name"
                  value={restaurantName}
           onChange={event => setRestaurantName(event.target.value)}    
              />
          </Form.Group>

          <Form.Group className="mb-3" controlId="restaurantLocationForm">
              <Form.Control type="text" placeholder="Location" />
          </Form.Group>

          <Form.Group className="mb-3" controlId="restaurantPriceRangeForm">
              <Form.Select aria-label="restaurantPriceRangeSelect">
                  <option>Price Range</option>
                  <option value="1">$</option>
                  <option value="2">$$</option>
                  <option value="3">$$$</option>
                  <option value="4">$$$$</option>
                  <option value="5">$$$$$</option>
              </Form.Select>
          </Form.Group>

          <Button variant="primary" type="submit">
              Add
          </Button>
    </Form>



    // <Form>
    //     <input          
    //       type="text"
    //       placeholder='Name'
    //       value={restaurantName}
    //       onChange={event => setRestaurantName(event.target.value)}
    //     />

    //     <input
    //       type="text"
    //       placeholder='Location'
    //       value={restaurantLocation}
    //       onChange={event => setRestaurantLocation(event.target.value)}
    //     />

    //     <select
    //       value={restaurantPriceRange}
    //       onChange={event => setRestaurantPriceRange(event.target.value)}
    //     >
    //         <option disabled>Price Range</option>
    //         <option value="1">$</option>
    //         <option value="2">$$</option>
    //         <option value="3">$$$</option>
    //         <option value="4">$$$$</option>
    //         <option value="5">$$$$$</option>
    //     </select>

    //     <Button
    //       variant='primary'
    //       type='submit'
    //       onClick={(event) => handleAdd(event)}
          
    //     >
    //       Add
    //     </Button>
    // </Form>
  )
}

export default AddRestaurantForm
