import React from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import ReviewRatingStars from './ReviewRatingStars'
import { FaChevronLeft, FaChevronRight, FaQuoteRight, FaTimes } from 'react-icons/fa'

const ReviewCards = ({reviews}) => {
  const {id} = useParams()

  const handleDeleteReview = async (id, reviewId) => {
    try {
      await axios.delete(`/${id}/reviews/${reviewId}`)
      window.location.reload(true);
    } catch (error) {
      console.log(error)
    }    
  }

  return (
    <section className="w-full h-32 rounded grid grid-cols-3 text-sm overflow-scroll overflow-x-hidden mt-3">
      {reviews && reviews.map(review => {
        return (
          <article className="flex flex-col items-center justify-center h-28 py-2 m-2 border shadow bg-blue-200 rounded" key={review.id}>
            <FaTimes
              className='text-red-600 self-end mr-2 cursor-pointer'
              onClick={() => handleDeleteReview(id, review.id)}
            />
            <ReviewRatingStars rating={review.rating} />
            <h3 className="font-bold text-xl mb-2 break-all" >{review.name}</h3>
            <p className="w-full h-24 px-2 text-gray-700 text-base break-all">
              {review.review}
            </p>
          </article>
        )
      })}        
    </section>
  )
}

export default ReviewCards
