import React from 'react'
import ReviewRatingStars from './ReviewRatingStars'

const ReviewCards = ({reviews}) => {

  
  return (
    <section className="w-full rounded grid grid-cols-3 text-sm">
      {reviews && reviews.map(review => {
        return (
          <article className="flex flex-col items-center justify-center py-2 m-1 border shadow bg-blue-200 rounded" key={review.id}>
            <ReviewRatingStars rating={review.rating} />
            <h3 class="font-bold text-xl mb-2 break-all" >{review.name}</h3>
            <p class="text-gray-700 text-base break-all">
              {review.review}
            </p>
          </article>
        )
      })}        
    </section>
  )
}

export default ReviewCards
