//react imports
import React, {useEffect} from 'react'
import { useParams, Link } from 'react-router-dom'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'

//bootstrap imports
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

const ReviewRestaurantForm = () => {
    const {id} = useParams()

    const {
        reviewerName, setReviewerName,        
        reviewerText, setReviewerText,
        reviewerRating, setReviewerRating
    } = useGlobalContext()

    useEffect(() => {
      setReviewerName('')
      setReviewerText('')
      setReviewerRating('Rating')
  }, [])

    const handleReview = async event => {
        event.preventDefault()
        try {
            await axios.post(`/${id}/addReview`, {
                name: reviewerName,
                review: reviewerText,
                rating: reviewerRating
            })

            setReviewerName('')
            setReviewerText('')
            setReviewerRating('Rating')
            window.location.reload(true);
        } catch (error) {
            console.log(error)
        }
    }   

    //rendering component
    return (
            <Form className='d-flex flex-column gap-4'>
            <Form.Group className="mx-1" controlId="reviwerNameForm">
                <Form.Control
                    type="text"
                    placeholder="Restaurant name"
                    value={restaurantName}
                    onChange={event => setRestaurantName(event.target.value)}    
                />
            </Form.Group>

            <Form.Group className="mx-1" controlId="restaurantLocationForm">
                <Form.Label>Location:</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Location"
                    value={restaurantLocation}
                    onChange={event => setRestaurantLocation(event.target.value)}
                />
            </Form.Group>

            <Form.Group className="mx-1" controlId="restaurantPriceRangeForm">
                <Form.Label>Price Range:</Form.Label>
                <Form.Select
                    aria-label="restaurantPriceRangeSelect"
                    className='text-muted text-truncate'
                    value={restaurantPriceRange}
                    onChange={event => setRestaurantPriceRange(event.target.value)}    
                >
                    <option>Price Range</option>
                    <option value="1">$</option>
                    <option value="2">$$</option>
                    <option value="3">$$$</option>
                    <option value="4">$$$$</option>
                    <option value="5">$$$$$</option>
                </Form.Select>
            </Form.Group>

             
            <Button className='align-self-start px-4' variant="primary" type='submit' onClick={event => {handleUpdate(event)}}> 
                <Link to={`/`} className='text-white text-decoration-none'>                       
                    Update   
                </Link>
            </Button>            
        </Form>

            // <form>
            //     <div className='row'>
            //         <label htmlFor="reviewerName">Name:</label>
            //         <input
            //             id='reviewerName'
            //             type="text"
            //             value={reviewerName}
            //             onChange={event => setReviewerName(event.target.value)}
            //         />

            //         <label htmlFor="rating">Rating:</label>
            //         <select
            //             id='rating'
            //             type="text"
            //             value={reviewerRating}
            //             onChange={event => setReviewerRating(event.target.value)}
            //         >
            //             <option disabled>Rating</option>
            //             <option value="1">1</option>
            //             <option value="2">2</option>
            //             <option value="3">3</option>
            //             <option value="4">4</option>
            //             <option value="5">5</option>
            //         </select>
            //     </div>

            //     <label htmlFor="review">Review:</label>
            //     <textarea
            //         id="review"
            //         cols="78"
            //         rows="10"
            //         value={reviewerText}
            //         onChange={event => setReviewerText(event.target.value)}
            //     />

            //     <button
            //         type='submit'
            //         onClick={event => handleReview(event)}
            //     >                    
            //         Submit                           
            //     </button>
            // </form>
    )
}

export default ReviewRestaurantForm
