import React, {useEffect} from 'react'
import { useParams, Link } from 'react-router-dom'
import { useGlobalContext } from '../globalContext'
import axios from '../apis/axios'

const UpdateRestaurantForm = () => {    
    const {id} = useParams()
    const {
        restaurantName, setRestaurantName,
        restaurantLocation, setRestaurantLocation,
        restaurantPriceRange, setRestaurantPriceRange,
    } = useGlobalContext()

    // Need to fetch the data inside this component.
    // Otherwise, if the user copy and paste the URL
    // in other tab, the browser won't be able to find the data.
    useEffect(() => {  
        const fetchData = async () => {
            try {
                const response = await axios.get(`/${id}`)             
                setRestaurantName(response.data.restaurant.name)
                setRestaurantLocation(response.data.restaurant[0].location)
                setRestaurantPriceRange(response.data.restaurant[0].price_range)
                
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()        
    }, [])
    
    const handleUpdate = async event => {
        event.preventDefault()
        try {
            await axios.put(`/${id}`, {
                name: restaurantName,
                location: restaurantLocation,
                price_range: restaurantPriceRange
        })
        } catch (error) {
            console.log(error)
        }        
    }

    return (
        <form className='update-form'>
            <div className='update-input'>
                <label htmlFor="restaurant-name">Name:</label>
                <input
                    id='restaurant-name'
                    type="text"
                    value={restaurantName}
                    onChange={event => setRestaurantName(event.target.value)}
                />
            </div>

            <div className='update-input'>
                <label htmlFor="restaurant-location">Location:</label>
                <input
                    id='restaurant-location'
                    type="text"
                    value={restaurantLocation}
                    onChange={event => setRestaurantLocation(event.target.value)}
                />
            </div>

            <div className='update-input'>
                <label htmlFor="price-range">Price Range</label>
                <select
                    id="price-range"
                    value={restaurantPriceRange}
                    onChange={event => setRestaurantPriceRange(event.target.value)}
                >
                    <option value="1">$</option>
                    <option value="2">$$</option>
                    <option value="3">$$$</option>
                    <option value="4">$$$$</option>
                    <option value="5">$$$$$</option>
                </select>
            </div>

            <button
                type='submit'
                onClick={(event) => handleUpdate(event)}
            >
                <Link
                    className='update-form-link'
                    to={`/`}>
                    Update
                </Link>
            </button>
        </form>
    )
}

export default UpdateRestaurantForm
