import React, {useEffect} from 'react'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const AddRestaurantForm = () => {
  const {
    restaurantName, setRestaurantName,
    restaurantLocation, setRestaurantLocation,
    restaurantPriceRange, setRestaurantPriceRange
  } = useGlobalContext()
  
  useEffect(() => {
      setRestaurantName('')
      setRestaurantLocation('')
      setRestaurantPriceRange('Price Range')
  }, [])

  const handleAdd = async event => {
    event.preventDefault()
    try {
        await axios.post('/', {
            name: restaurantName,
            location: restaurantLocation,
            price_range: restaurantPriceRange,
        })
        window.location.reload(true);
    } catch (error) {
        console.log(error)
    }
  }

  return (
      <Form className='d-flex'>
          <Form.Group className="mb-3" controlId="restaurantNameForm">
              <Form.Control type="text" placeholder="Restaurant name" />
          </Form.Group>

          <Form.Group className="mb-3" controlId="restaurantLocationForm">
              <Form.Control type="password" placeholder="Password" />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
    </Form>



    // <Form>
