      <article className="flex flex-col items-center justify-center h-28 py-2 m-2 border shadow bg-blue-200 rounded" key={reviews[reviewIndex].id}>
            <FaChevronLeft
              className=''
              onClick={prevReview}
            />
            <div className=''>
              {/* <div className=''>
                <img src={image} alt={name} />
                <span className='quote-icon'>
                  <FaQuoteRight />
                </span>
              </div> */}
            <ReviewRatingStars rating={reviews[reviewIndex].rating} />
