import React from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'

const ReviewRestaurantForm = () => {
    const {id} = useParams()
    const {
        reviewerName, setReviewerName,
        reviewerRating, setReviewerRating,
        reviewerText, setReviewerText
    } = useGlobalContext()

    const handleReview = async event => {
        event.preventDefault()
        try {
            await axios.put(`/${id}/addReview`, {
                name: reviewerName,
                review: reviewerRating,
            })
        } catch (error) {
            console.log(error)
        }
    }   

    return (
        <section className='review-form'>
            <form>
                <div className='row'>
                    <label htmlFor="reviewerName">Name:</label>
                    <input
                        id='reviewerName'
                        type="text"
                        value={reviewerName}
                        onChange={event => setReviewerName(event.target.value)}
                    />

                    <label htmlFor="rating">Rating:</label>
                    <select
                        id='rating'
                        type="text"
                        value={reviewerRating}
                        onChange={event => setReviewerRating(event.target.value)}
                    >
                        <option disabled>Rating</option>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>
                </div>

                <label htmlFor="review">Review:</label>
                <textarea
                    id="review"
                    cols="78"
                    rows="10"
                    value={reviewerText}
                    onChange={event => setReviewerText(event.target.value)}
                />

                <button
                    type='submit'
                    onClick={event => handleReview(event)}
                >
                    Submit
                </button>
            </form>
        </section>
    )
}

export default ReviewRestaurantForm
