import React, {useEffect, useCallback} from 'react'
import {Link} from 'react-router-dom'
import axios from '../apis/axios' //from axios to fetch the data
import {useGlobalContext} from '../globalContext'

const RestaurantList = () => {
    const {restaurants, setRestaurants} = useGlobalContext()
    
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('/')
                setRestaurants(response.data.data.restaurants)
            } catch (error) {
                console.log(error)
            }
        }
        fetchData()
    }, [restaurants])

    // The data fetching above has an infinite loop.
    // Tried to fix that with useCallback below, but did not work.
    // const fetchData = useCallback(async () => {
    //     console.log(restaurants)
    //         try {
    //             const response = await axios.get('/')
    //             setRestaurants(response.data.data.restaurants)
    //         } catch (error) {
    //             console.log(error)
    //         }
    //     }, [restaurants])
    //     useEffect(() => {
    //         fetchData()
    //     }, [restaurants, fetchData])        

    const handleDelete = async id => {
        try {
            await axios.delete(`/${id}`)
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <section>
            <table>
                <thead>
                    <tr className='header-tr'>
                        <th>Restaurant</th>
                        <th>Location</th>
                        <th>Price Range</th>
                        <th>Ratings</th>
                        <th>Review</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {restaurants && restaurants.map(restaurant => {
                        return (
                        <tr key={restaurant.id}>
                            <td>{restaurant.name}</td>
                            <td>{restaurant.location}</td>
                            <td>{'$'.repeat(restaurant.price_range)}</td>
                            <td>reviews</td>
                            <td>
                                <button className="review-button" >
                                    <Link
                                        className='revi' 
                                        to={`/restaurants/${restaurant.id}`}>
                                        Review
                                    </Link>
                                </button>
                            </td>
                            <td>
                                <button className="update-button" >
                                    <Link to={`/restaurants/${restaurant.id}/update`}>
                                        Update
                                    </Link>
                                </button>
                            </td>
                            <td>
                                <button
                                    className="delete-button"
                                    onClick={() => handleDelete(restaurant.id)}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                        )                        
                    })}
                </tbody>
            </table>
        </section>
    )
}

export default RestaurantList
