//react imports
import React, {useEffect} from 'react'
import { useParams, Link } from 'react-router-dom'
import { useGlobalContext } from '../globalContext'
import axios from '../apis/axios'

//bootstrap imports
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

const UpdateRestaurantForm = () => {    
    const {id} = useParams()
    const {
        restaurantName, setRestaurantName,
        restaurantLocation, setRestaurantLocation,
        restaurantPriceRange, setRestaurantPriceRange,
    } = useGlobalContext()

    // Need to fetch the data inside this component.
    // Otherwise, if the user copy and paste the URL
    // in other tab, the browser won't be able to find the data.
    useEffect(() => {  
        const fetchData = async () => {
            try {
                const response = await axios.get(`/${id}`)             
                setRestaurantName(response.data.restaurant.name)
                setRestaurantLocation(response.data.restaurant.location)
                setRestaurantPriceRange(response.data.restaurant.price_range)
                
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()        
    }, [])
    
    const handleUpdate = async event => {
        event.preventDefault()
        try {
            await axios.put(`/${id}`, {
                name: restaurantName,
                location: restaurantLocation,
                price_range: restaurantPriceRange
        })
        } catch (error) {
            console.log(error)
        }        
    }

    //rendering component
    return (
        <form className="w-full flex flex-col gap-10 rounded py-6 mt-6 text-sm">
            <div>
            <label class="block text-gray-700 text-sm font-bold mb-2" for="restaurantName">
                Restaurant name:
            </label>
            <input
              className="appearance-none shadow col-span-4                                               
                        border border-gray-400 hover:border-blue-400 rounded
                        px-1 py-2 
                        text-gray-700 leading-tight
                        focus:outline-none focus:shadow-outline"
                id='restaurantName'
                type="text"
                placeholder="Restaurant name"
                value={restaurantName}
                onChange={event => setRestaurantName(event.target.value)}
            />
            </div>
            
            <input
              className="appearance-none shadow col-span-4                                              
                        border border-gray-400 hover:border-blue-400 rounded
                        px-1 py-2 
                        text-gray-700 leading-tight
                        focus:outline-none focus:shadow-outline"
                type="text"
                placeholder="Location"
                value={restaurantLocation}
                onChange={event => setRestaurantLocation(event.target.value)}
            />

            <select
              className="appearance-none shadow col-span-3 
                    border border-gray-400 hover:border-blue-400 rounded
                    px-1 py-2 
                    text-gray-700 leading-tight
                    focus:outline-none focus:shadow-outline"
                value={restaurantPriceRange}
                onChange={event => setRestaurantPriceRange(event.target.value)}
            >
                <option>Price Range</option>
                <option value="1">$</option>
                <option value="2">$$</option>
                <option value="3">$$$</option>
                <option value="4">$$$$</option>
                <option value="5">$$$$$</option>
            </select>
        
            <button
              className="rounded col-span-1 
                        bg-blue-500 hover:bg-blue-700 text-white
                        font-bold
                        px-1 py-2              
                        focus:outline-none focus:shadow-outline"
                type="submit"
                onClick={event => handleUpdate(event)}
            >
                Add
            </button>
        </form>

        // <Form className='d-flex flex-column gap-4'>
        //     <Form.Group className="mx-1" controlId="restaurantNameForm">
        //         <Form.Label>Restaurant name:</Form.Label>
        //         <Form.Control
        //             type="text"
        //             placeholder="Restaurant name"
        //             value={restaurantName}
        //             onChange={event => setRestaurantName(event.target.value)}    
        //         />
        //     </Form.Group>

        //     <Form.Group className="mx-1" controlId="restaurantLocationForm">
        //         <Form.Label>Location:</Form.Label>
        //         <Form.Control
        //             type="text"
        //             placeholder="Location"
        //             value={restaurantLocation}
        //             onChange={event => setRestaurantLocation(event.target.value)}
        //         />
        //     </Form.Group>

        //     <Form.Group className="mx-1" controlId="restaurantPriceRangeForm">
        //         <Form.Label>Price Range:</Form.Label>
        //         <Form.Select
        //             aria-label="restaurantPriceRangeSelect"
        //             className='text-muted text-truncate'
        //             value={restaurantPriceRange}
        //             onChange={event => setRestaurantPriceRange(event.target.value)}    
        //         >
        //             <option>Price Range</option>
        //             <option value="1">$</option>
        //             <option value="2">$$</option>
        //             <option value="3">$$$</option>
        //             <option value="4">$$$$</option>
        //             <option value="5">$$$$$</option>
        //         </Form.Select>
        //     </Form.Group>

             
        //     <Button className='align-self-start px-4' variant="primary" type='submit' onClick={event => {handleUpdate(event)}}> 
        //         <Link to={`/`} className='text-white text-decoration-none'>                       
        //             Update   
        //         </Link>
        //     </Button>            
        // </Form>
    )
}

export default UpdateRestaurantForm
