import React from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import ReviewRatingStars from './ReviewRatingStars'

const ReviewCards = ({reviews}) => {
  const {id, reviewId} = useParams()

  const handleDeleteReview = async (id, reviewId) => {
    console.log('clicked', id, reviewId)
    try {
      await axios.delete(`/${id}/reviews/${reviewId}`)
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <section className="w-full rounded grid grid-cols-3 text-sm">
      {reviews && reviews.map(review => {
        return (
          <article className="flex flex-col items-center justify-center py-2 m-1 border shadow bg-blue-200 rounded" key={review.id}>
            <button onClick={() => handleDeleteReview(id, review.id)}>X</button>
            <ReviewRatingStars rating={review.rating} />
            <h3 className="font-bold text-xl mb-2 break-all" >{review.name}</h3>
            <p className="text-gray-700 text-base break-all">
              {review.review}
            </p>
          </article>
        )
      })}        
    </section>
  )
}

export default ReviewCards
