import React from 'react'

const ReviewRestaurantHeader = () => {
  return (
    <div>
        <h1 className='text-3xl'>Review Page</h1>
        <h4 className='text-xl'>{selectedRestaurant.restaurant.name} - {selectedRestaurant.restaurant.location}</h4>
        <div>
            <ReviewRatingStars rating={selectedRestaurant.restaurant.average_rating} />
            <span>
                {
                    selectedRestaurant.restaurant.total_ratings ?
                        `(${selectedRestaurant.restaurant.total_ratings} reviews)` :
                        '(0 reviews)'
                }
            </span>
        </div>
    </div>
  )
}

export default ReviewRestaurantHeader
