import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from '../apis/axios'
import { useGlobalContext } from '../globalContext'

const ReviewRestaurantForm = () => {
    const {id} = useParams()
    const {name, setName} = useGlobalContext()
    console.log(name)
    
    // Need to fetch the data inside this component.
    // Otherwise, if the user copy and paste the URL
    // in other tab, the browser won't be able to find the data.
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`/${id}`)
                console.log(response.data.data.restaurant.name)
                setName(response.data.data.restaurant[0].name)
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()
    }, [])

    const handleReview = event => {
        event.preventDefault()
    }

    

    return (
        <section>
            {name}
            <form>
                <div></div>
                <label htmlFor="name">Name:</label>
                <input id='name' type="text" />

                <label htmlFor="rating">Rating:</label>
                <select id='rating' type="text">
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                </select>

                <label htmlFor="review"></label>
                <textarea id="review" cols="30" rows="10"></textarea>
            </form>
        </section>
    )
}

export default ReviewRestaurantForm
