import React, {useEffect} from 'react'
import ReviewRestaurantForm from '../components/ReviewRestaurantForm'
import ReviewRatingStars from '../components/ReviewRatingStars'
import ReviewCards from '../components/ReviewCards'
import axios from '../apis/axios'
import { useParams } from 'react-router-dom'
import { useGlobalContext } from '../globalContext'

const ReviewRestaurantPage = () => {
  const {id} = useParams()
  const {selectedRestaurant, setSelectedRestaurant} = useGlobalContext()

  useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`/${id}`)
                setSelectedRestaurant(response.data)
            } catch (error) {
                console.log(error)
            }            
        }
        fetchData()
    }, [])

  //rendering component
  return (
    <>
      {selectedRestaurant && (
        <section className='d-flex flex-column align-items-center'>
          <h1 className='text-center'>Review Page</h1>
          <h4 className='text-center'>{selectedRestaurant.restaurant.name} - {selectedRestaurant.restaurant.location}</h4>
          <div>
              <ReviewRatingStars rating={selectedRestaurant.restaurant.average_rating} />
              <span>
                {
                  selectedRestaurant.restaurant.total_ratings ?
                  `(${selectedRestaurant.restaurant.total_ratings} reviews)` :
                  '(0 reviews)'
                }
              </span>
          </div>
          <ReviewCards reviews={selectedRestaurant.reviews} />
          <ReviewRestaurantForm />    
        </section>
      )}
    </>
  )
}

export default ReviewRestaurantPage
