    const handleDelete = async id => {
        try {
            await axios.delete(`/${id}`)
        } catch (error) {
            console.log(error)
        }
    }
    
    const renderRating = restaurant => {
        if (!restaurant.total_ratings) {
            return <span>0 ratings</span> 
        } else {
            return (
                <>
                    <ReviewRatingStars rating={restaurant.average_rating} />
                    <span>({restaurant.total_ratings})</span>
                </>
            )
        }              
    }

    return (
        <Table striped bordered hover>
        <thead>
            <tr className='header-tr'>
                <th>Restaurant</th>
                <th>Location</th>
                <th>Price Range</th>
                <th>Ratings</th>
                <th>Review</th>
                <th>Edit</th>
                <th>Delete</th>
            </tr>
        </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan={2}>Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</Table>


